import React, { useState } from 'react';
import './css/Carousel.css';

const Carousel = ({ questions }) => {
    const [currentIndex, setCurrentIndex] = useState(0);

    const goToPrev = () => {
        setCurrentIndex((prevIndex) => (prevIndex === 0 ? questions.length - 1 : prevIndex - 1));
    };

    const goToNext = () => {
        setCurrentIndex((prevIndex) => (prevIndex === questions.length - 1 ? 0 : prevIndex + 1));
    };

    return (
        <div className='box'>
            <div className="carousel">
                <button className="prev" onClick={goToPrev}>&lt;</button>
                <div className="question-container">
                    <div className="question" style={{ backgroundColor: 'rgb(252, 228, 196)' }}>
                        {questions[currentIndex]}
                    </div>
                </div>
                <button className="next" onClick={goToNext}>&gt;</button>
            </div>
        </div>
    );
};

export default Carousel;
