import React, { useState } from 'react';

const Slider = ({ questions }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const goToPrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? questions.length - 1 : prevIndex - 1));
  };

  const goToNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === questions.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className="carousel">
      <button onClick={goToPrev}>&lt;</button>
      <div className="question">{questions[currentIndex]}</div>
      <button onClick={goToNext}>&gt;</button>
    </div>
  );
};

export default Slider ;
