import React, { useState } from 'react';
import './css/Carousel.css';

const Carousel = ({ questions }) => {
    const [currentIndex, setCurrentIndex] = useState(0);

    const goToPrev = () => {
        setCurrentIndex((prevIndex) => (prevIndex === 0 ? questions.length - 1 : prevIndex - 1));
    };

    const goToNext = () => {
        setCurrentIndex((prevIndex) => (prevIndex === questions.length - 1 ? 0 : prevIndex + 1));
    };

    const handleOptionClick = (optionIndex) => {
        // Handle option selection if needed
    };

    return (
        <div className="carousel">
            <button className="prev" onClick={goToPrev}>&lt;</button>
            <div className="question-container">
                <div className="question" >
                    {questions[currentIndex].questionText}
                </div>
                <div className="options">
                    {questions[currentIndex].options.map((option, index) => (
                        <div key={index} className="option" style={{display:"flex" , fl}}>
                            {option.type === 'radio' ? (
                                <input
                                    type="radio"
                                    name={`question_${currentIndex}`}
                                    value={option.text}
                                    onClick={() => handleOptionClick(index)}
                                />
                            ) : (
                                <button onClick={() => handleOptionClick(index)}>{option.text}</button>
                            )}
                        </div>
                    ))}
                </div>
            </div>
            <button className="next" onClick={goToNext}>&gt;</button>
        </div>
    );
};

export default Carousel;
