import React, { useState, useEffect } from 'react';
import './css/Carousel.css';
import { useNavigate } from 'react-router-dom';

const Carousel = ({ questions }) => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const navigate = useNavigate()

    const goToPrev = () => {
        setCurrentIndex((prevIndex) => (prevIndex === 0 ? questions.length - 1 : prevIndex - 1));
    };

    const goToNext = () => {
        if (currentIndex === questions.length - 1) {
            navigate("/final"); // Redirect to final score page
        } else {
            setCurrentIndex((prevIndex) => prevIndex + 1);
        }
    };

    const handleOptionClick = (optionIndex) => {
        const selectedOption = questions[currentIndex].options[optionIndex].text;
        // Construct a string to represent the selected question and option
        const selectedData = `${questions[currentIndex].questionText}:${selectedOption}`;
        // Store the selected data in a cookie
        document.cookie = `selectedOptions=${selectedData}`;
    };

    useEffect(() => {
        // Redirect to final score page if user directly goes to the 7th question
        if (currentIndex === questions.length - 1) {
            navigate("/final");
        }
    }, [currentIndex, questions.length]);

    return (
        <div className="carousel">
            <button className="prev" onClick={goToPrev}>&lt;</button>
            <div className="question-container">
                <div className="question" >
                    {questions[currentIndex].questionText}
                </div>
                <div className="options">
                    {questions[currentIndex].options.map((option, index) => (
                        <div key={index} className="option">
                            <input
                                type="radio"
                                id={`option_${index}`}
                                name={`question_${currentIndex}`}
                                value={option.text}
                                onClick={() => handleOptionClick(index)}
                            />
                            <label htmlFor={`option_${index}`}>{option.text}</label>
                        </div>
                    ))}
                </div>
            </div>
            <button className="next" onClick={goToNext}><Icon icon="ic:sharp-play-arrow"   /></button>
        </div>
    );
};

export default Carousel;
